event_handlers.script:
if 'boulder' in event.data.get('full_command', ''):
    if not player.get_property('strength', 0) >= 5:
        response = 'You strain against the boulder but lack the strength to move it. Perhaps you need to enhance your abilities somehow.'
    else:
        response = None
else:
    response = None

if player.get_property('poisoned'):
    player.set_property('poisoned', False)
    player.set_property('health', player.get_property('health', 100) + 20)
    response = '\nThe magical aura of the garden heals your ailments! You feel much better.'
else:
    response = None

if event.data.get('verb') == 'read' and 'scroll' in event.data.get('full_command', ''):
    if player.get_property('magical_knowledge') and player.get_property('seen_elvish'):
        scroll = None
        current_room = game_state.get_room(player.current_room)
        for item in current_room.items + player.inventory:
            if 'scroll' in item.name.lower():
                scroll = item
                break
        if scroll and not scroll.get_property('translated'):
            scroll.set_property('translated', True)
            response = '\nWith your magical knowledge and familiarity with elvish, the ancient words become clear: "By the power of the ancients, let a doorway be opened between realms."'
        else:
            response = None
    else:
        response = None
else:
    response = None

room = game_state.get_room('wizard_study')
original_desc = 'You stand in a cluttered wizard\'s study filled with books, scrolls, and magical artifacts. Candles flicker mysteriously without any wind. A heavy oak desk dominates the center of the room.'

if game_state.get_flag('lever_activated'):
    room.description = original_desc + ' A section of the wall has slid away, revealing a secret passage.'
elif game_state.get_flag('orb_activated'):
    room.description = original_desc + ' The room now glows with residual magical energy from your crystal orb activation.'
else:
    room.description = original_desc

response = None

if event.data.get('item1') and 'sword' in event.data['item1'].name and event.data['item1'].get_property('enchanted'):
    if player.get_property('cursed'):
        player.set_property('cursed', False)
        player.set_property('luck', 0)
        player.score += 50
        response = '\nThe enchanted sword glows brightly! Its pure magic breaks the curse that afflicted you. You feel the dark influence lift from your soul.'
    else:
        response = None
else:
    response = None
